function addEditorFunctionality(html) {
  const richTextField = html.querySelector("#richTextField");
  richTextField.contentDocument.designMode ='On';

  const handleArgAction = (e)=>{
    const command = e.target.dataset.value;
    const arg = e.target.value;
    richTextField.contentDocument.execCommand(command, false, arg);
  }
  
  const handleAction = (e)=>{
    const command = e.target.value;
    richTextField.contentDocument.execCommand(command, false, null);
  }

  html.querySelectorAll(".cmdbtn").forEach((button)=>{
    button.addEventListener("click",handleAction);
  })

  html.querySelectorAll(".cmdargbtn").forEach((button)=>{
    button.addEventListener("change",handleArgAction)
  })

  html.querySelector("#pdf").addEventListener('click', (event) => {
    richTextField.contentWindow.focus();
    richTextField.contentWindow.print();
  });

  html.querySelector("#pic").addEventListener('click', (event) => {
    var player = $('iframe#my-video-frame').contents()[0].querySelector('.video-stream');

    var canvas = document.createElement("canvas");
    canvas.width = 400;
    canvas.height = 225;
    canvas.getContext('2d').drawImage(player, 0, 0, 400, 225);
  
    var base64Canvas = canvas.toDataURL("image/jpeg");

    richTextField.contentDocument.execCommand('insertImage', false, base64Canvas);
  } );
}


function addScreen(element) {
  let modalElt = document.createElement("div");
  modalElt.classList.add("modal");
  modalElt.setAttribute("id", "myModal");

  let modalContent = document.createElement("div");
  modalContent.classList.add("modal-content");

  modalElt.appendChild(modalContent);

  element.appendChild(modalElt);
}

function openModal() {
  addScreen(document.body);

  let video = document.getElementsByClassName("video-stream")[0];

  // Get the modal
  let modal = document.getElementById("myModal");

  // Get the button that opens the modal
  let btn = document.getElementById("myBtn");

  // Get the <span> element that closes the modal
  let span = document.getElementsByClassName("close")[0];

  let modalContent = document.getElementsByClassName("modal-content")[0];

  // When the user clicks on the button, open the modal
  btn.addEventListener('click', function (e) {
    let videoId = window.location.href.split("=")[1];

    let player = document.getElementsByClassName("video-stream")[0];
    player.pause();
    let currentTime = player.currentTime;
    currentTime = Math.floor(currentTime);

    let iframeElt = document.createElement("iframe");
    iframeElt.src = `https://www.youtube.com/embed/${videoId}?start=${currentTime}&autoplay=1&showinfo=0&rel=0&fs=0&autohide=0`;

    iframeElt.frameborder = 0;
    iframeElt.setAttribute("id", "my-video-frame");
    modalContent.appendChild(iframeElt);

    modal.style.display = "block";

    
      // notepad

    $('.modal-content').append(`
      <iframe id="myframe" name="myframe" style="width: 100%; height: 95%;"></iframe>  
    `);

    const editor = chrome.runtime.getURL("editor/editor.html");
   
    fetch(editor)
        .then((response) => response.text())
        .then((text) => {
           return ($("#myframe").contents().find('html').html(text)[0]);
        }).then(addEditorFunctionality);
  });
}
